void printBracket(const vector<string>& bracket, int round) {
    cout << "\nRound " << round << " Matches:" << endl;
    for (size_t i = 0; i < bracket.size(); i += 2) {
        cout << bracket[i] << " vs " << bracket[i + 1] << endl;
    }
}

vector<string> generateBracket(vector<string> participants) {
    int size = participants.size();
    int nextPowerOfTwo = pow(2, ceil(log2(size)));

    while (participants.size() < nextPowerOfTwo) {
        participants.push_back("BYE"); // Fill with BYEs to fit bracket size
    }

    random_shuffle(participants.begin(), participants.end());
    return participants;
}

string getWinner(const string& option1, const string& option2) {
	string input;
	while (true) {
		cout << "Winner of " << option1 << " (1) vs " << option2 << " (2): ";
		getline(cin, input);
		if (input == "1") return option1;
		if (input == "2") return option2;
		cout << "Invalid input. Please enter 1 or 2." << endl;
	}
}

int runBracketGenerator() {
	vector<string> allParticipants = chooseBracketLists();
	vector<string> participants;
	int num;

	cout << "Enter number of participants: (up to " << allParticipants.size() << "):";
	cin >> num;
	cin.ignore(); // Ignore newline
	std::random_device rd;
	std::mt19937 g(rd());
	std::shuffle(allParticipants.begin(), allParticipants.end(), g);

	for (int i = 0; i < num; i++) {
		participants.push_back(allParticipants.at(i));
	}

	vector<string> bracket = generateBracket(participants);
	int round = 1;
	while (bracket.size() > 1) {
		printBracket(bracket, round++);
		vector<string> nextRound;
		for (size_t i = 0; i < bracket.size(); i += 2) {
			nextRound.push_back(getWinner(bracket[i], bracket[i + 1]));
		}
		shuffle(nextRound.begin(), nextRound.end(), g); // Shuffle after each round
		bracket = nextRound;
	}

	cout << "\nChampion: " << bracket[0] << "!" << endl;
	return 0;
}
